import BlogPost from "./BlogPost";
import Pagination from "./Pagination";
import React, { useState } from "react";
import blogs from "../data/blogs.json";

const PAGE_SIZES = [15, 25, 50, 100];
const siblingCount = 1;

function BlogList() {
  const [currentPage, setCurrentPage] = useState(1);
  const [currentPaginationData, setCurrentPaginationData] = useState(blogs.posts.slice(0, 15));
  const [lastPage, setLastPage] = useState(Math.ceil(blogs.posts.length / 15));
  const [pageSize, setPageSize] = useState(15);

  const updatePage = (page, size) => {
    const indexOfLastRecord = page * size;
    const indexOfFirstRecord = indexOfLastRecord - size;
    setCurrentPaginationData(blogs.posts.slice(indexOfFirstRecord, indexOfLastRecord));
  };

  const updateRowsPerPage = (size) => {
    setCurrentPage(1);
    setLastPage(Math.ceil(blogs.posts.length / size));
    setPageSize(size);
    updatePage(1, size);
  };

  const updatePageNumber = (action) => {
    const page = action === 'increment' ?
      (currentPage < lastPage ?
        currentPage + 1:
        lastPage) :
      (currentPage > 1 ?
      currentPage - 1:
      1);
    setCurrentPage(page);
    updatePage(page, size);
  };

  const updateCurrentPage = (page) => {
    setCurrentPage(page);
    updatePage(page, pageSize);
  };

  return (
    <div>
      <Pagination
        currentPage={currentPage}
        lastPage={lastPage} //pageCount
        onPageMove={updateCurrentPage} //
        onPageNumberChange={updatePageNumber}
        onPageSizeOptionChange={updateRowsPerPage}
        pageSize={parseInt(pageSize)}
        pageSizeOptions={PAGE_SIZES}
        siblingCount={SIBILING_COUNT}
        totalCount={blogs.posts.length}
      />
      <ul
        // Do not modify the aria-label below, it is used for Hatchways automation.
        aria-label="blog list"
        >
        {currentPaginationData.map((blog) => (
          <BlogPost
            key={blog.id}
            author={blog.author}
            title={blog.title}
            excerpt={blog.excerpt}
            featureImage={blog.image}
          />
        ))}
      </ul>
    </div>
  );
}

export default BlogList;
