import "../css/pagination.scss";

import { ChevronLeftIcon, ChevronRightIcon } from "@heroicons/react/outline";
import usePagination, { DOTS } from "../hooks/usePagination";


import PropTypes from "prop-types";
import React from "react";
import { nanoid } from "nanoid";
import { func } from 'prop-types';

function Pagination({
  currentPage,
  lastPage,
  onPageMove,
  onPageNumberChange,
  onPageSizeOptionChange,
  pageSize,
  pageSizeOptions,
  totalCount
}) {

  const usePagination = (
    currentPage,
    lastPage,
    siblingCount
    ) => {

    const leftSiblingIndex = Math.max(currentPage - siblingCount, 1);
    const rightSiblingIndex = Math.min(currentPage + siblingCount, lastPage);
    const shouldShowLeftDots = leftSiblingIndex > 1;
    const shouldShowRightDots = rightSiblingIndex < lastPage - 1;

    const firstPageIndex = 1;
    const lastPageIndex = lastPage;

    if (!shouldShowLeftDots && shouldShowRightDots) {
      const leftItemCount = 1 + 2 * siblingCount;
      const leftRange = [...Array(leftItemCount).keys()].map(i => i + leftSiblingIndex)
      return [...leftRange, DOTS, lastPage];
    }

    if (shouldShowLeftDots && !shouldShowRightDots) {

      let rightItemCount = 1 + 2 * siblingCount;
      let rightRange = [...Array(rightItemCount).keys()].map(i => i + lastPage - rightItemCount + 1)
      return [firstPageIndex, DOTS, ...rightRange];
    }

    if (shouldShowLeftDots && shouldShowRightDots) {
      let middleRange = [...Array(3).keys()].map(i => i + leftSiblingIndex)
      range(leftSiblingIndex, rightSiblingIndex);
      return [firstPageIndex, DOTS, ...middleRange, DOTS, lastPageIndex];
    }

    return []

  };

  const paginationRange = usePagination(
    currentPage,
    totalCount,
    1
  );

  console.log('up', paginationRange)

  const onNext = () => {
    onPageNumberChange('increment');
  };

  const onPrevious = () => {
    onPageNumberChange('decrement');
  };

  return (
    <ul
      className="wrapper"
      // Do not modify the aria-label below, it is used for Hatchways automation.
      aria-label="Blog post pagination list"
    >
      <li className="paginationItem">
        <button
          type="button"
          className="arrowButton left"
          // Do not modify the aria-label below, it is used for Hatchways automation.
          aria-label="Goto previous page"
          onClick={onPrevious}
          disabled={currentPage === 1} // change this line to disable a button.
        >
          <ChevronLeftIcon />
        </button>
      </li>

      {paginationRange.map((pageNumber) => {
        // console.log('pn', pageNumber)
        const key = nanoid();
        // console.log('k', key)

        if (pageNumber === DOTS) {
          return (
            <li key={key} className="dots">
              &#8230;
            </li>
          );
        }

        return (
          <li
            key={key}
            className="paginationItem"
            aria-current={pageNumber===currentPage ? 'page' : ''} // change this line to highlight a current page.
          >
            <button
              type="button"
              // Do not modify the aria-label below, it is used for Hatchways automation.
              aria-label={`Goto page ${pageNumber}`}
              onClick={(e) => onPageMove(parseInt(e.target.innerText))}
            >
              {pageNumber}
            </button>
          </li>
        );
      })}

      <li className="paginationItem">
        <button
          type="button"
          className="arrowButton right"
          // Do not modify the aria-label below, it is used for Hatchways automation.
          aria-label="Goto next page"
          onClick={onNext}
          disabled={currentPage === lastPage} // change this line to disable a button.
        >
          <ChevronRightIcon />
        </button>
      </li>

      <select
        className="paginationSelector"
        // Do not modify the aria-label below, it is used for Hatchways automation.
        aria-label="Select page size"
        // value={pageSize}
        onChange={(e) => {
          onPageSizeOptionChange(e.target.value);
        }}
      >
        {pageSizeOptions.map((size) => (
          <option key={size} defaultValue={pageSize === size} value={size}>
            {size} per page
          </option>
        ))}
      </select>
    </ul>
  );
}

Pagination.propTypes = {
  currentPage: PropTypes.number,
  lastPage: PropTypes.number,
  onPageMove: PropTypes.func,
  onPageNumberChange: PropTypes.func,
  onPageSizeOptionChange: PropTypes.func,
  pageSize: PropTypes.number,
  pageSizeOptions: PropTypes.instanceOf(Array),
  totalCount: PropTypes.number,
};

Pagination.defaultProps = {
  currentPage: 1,
  lastPage: 44,
  onPageMove: () => {},
  onPageNumberChange: () => {},
  onPageSizeOptionChange: () => {},
  pageSize: 15,
  pageSizeOptions: [15, 25, 50, 100],
  totalCount: 0,
};

export default Pagination;
