import BlogPost from "./BlogPost";
import Pagination from "./Pagination";
import React, { Component } from "react";
import blogs from "../data/blogs.json";

const PAGE_SIZES = [15, 25, 50, 100];

class BlogList extends Component {
  constructor(){
    super();
    this.state = {
      currentPage: 1,
      currentPaginationData: blogs.posts.slice(0, 15),
      lastPageNumber: Math.ceil(blogs.posts.length / 15),
      pageSize:15
    };
  };

  updatePage = (page, size) => {
    console.log('updt', page, size)
    const indexOfLastRecord = page * size;
    const indexOfFirstRecord = indexOfLastRecord - size;
    this.setState({currentPaginationData: blogs.posts.slice(indexOfFirstRecord, indexOfLastRecord)});
  };

  updateRowsPerPage = (size) => {
    this.setState({
      currentPage: 1,
      lastPageNumber: Math.ceil(blogs.posts.length / size),
      pageSize: size
    }); // we can just update the pageSize and have the currentPage as is
    this.updatePage(1, size);
  };

  updatePageNumber = (action) => {
    const currentPage = this.state.currentPage;
    const lastPage = this.state.lastPageNumber;
    const size = this.state.pageSize;
    const page = action === 'increment' ?
      (currentPage < lastPage ?
        currentPage + 1:
        lastPage) :
      (currentPage > 1 ?
      currentPage - 1:
      1);
    this.setState({currentPage: page});
    this.updatePage(page, size);
  };

  updateCurrentPage = (page) => {
    console.log('pg', )
    const size = this.state.pageSize;
    this.setState({currentPage: page});
    this.updatePage(page, size);
  };

  render() {
    return (
      <div>
        <Pagination
          currentPage={this.state.currentPage}
          lastPage={this.state.lastPageNumber}
          onPageMove={this.updateCurrentPage}
          onPageNumberChange={this.updatePageNumber}
          onPageSizeOptionChange={this.updateRowsPerPage}
          pageSize={this.state.pageSize}
          pageSizeOptions={PAGE_SIZES}
          totalCount={blogs.posts.length}
        />
        <ul
          // Do not modify the aria-label below, it is used for Hatchways automation.
          aria-label="blog list"
          >
          {this.state.currentPaginationData.map((blog) => (
            <BlogPost
              key={blog.id}
              author={blog.author}
              title={blog.title}
              excerpt={blog.excerpt}
              featureImage={blog.image}
            />
          ))}
        </ul>
      </div>
    );
  };
}

export default BlogList;
