import PropTypes from 'prop-types';
export const DOTS = "...";

function usePagination(currentPage, pageSize, totalCount) {
  /*
    Rewrite the logic here to map out the pagination to be displayed.

  */
    const SIBILING_COUNT = 1;
    const lastPage = Math.ceil(totalCount / size)
    const leftSiblingIndex = Math.max(currentPage - SIBILING_COUNT, 1);
    const rightSiblingIndex = Math.min(currentPage + SIBILING_COUNT, lastPage);
    const shouldShowLeftDots = leftSiblingIndex > 1;
    const shouldShowRightDots = rightSiblingIndex < lastPage;
    const firstPageIndex = 1;
    const lastPageIndex = lastPage;

    if (!shouldShowLeftDots && shouldShowRightDots) {
      const leftItemCount = 1 + 2 * SIBILING_COUNT;
      const leftRange = [...Array(leftItemCount).keys()].map(i => i + leftSiblingIndex)
      return [...leftRange, DOTS, lastPage].map(String);
    }

    if (shouldShowLeftDots && !shouldShowRightDots) {
      let rightItemCount = 1 + 2 * SIBILING_COUNT;
      let rightRange = [...Array(rightItemCount).keys()].map(i => i + lastPage - rightItemCount + 1)
      return [firstPageIndex, DOTS, ...rightRange].map(String);
    }

    if (shouldShowLeftDots && shouldShowRightDots) {
      let middleRange = [...Array(3).keys()].map(i => i + leftSiblingIndex)
      return [firstPageIndex, DOTS, ...middleRange, DOTS, lastPageIndex].map(String);
    }

    if (!shouldShowLeftDots && !shouldShowRightDots) {
      return [1];
    }
}

usePagination.propTypes = {
  currentPage: PropTypes.number,
  pageSize: PropTypes.number,
};

usePagination.defaultProps = {
  currentPage: 1,
  pageSize: 15,
};

export default usePagination;
